package org.opentutorials.javatutorials.array;

public class LengthDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] blping = new String[4];
		blping[0]="Jenny";
		System.out.println(blping.length);
		blping[1]="Jisoo";
		System.out.println(blping.length);
		blping[2]="Rose";
		System.out.println(blping.length);
		blping[3]="Lisa";
		System.out.println(blping.length);
	}

}
